function toggleMenu() {
    var a = $("#slideoutMenu");
    if (a.is(":visible")) {
        $(".menu-visible").css("visibility", "hidden");
        $(".menu-hidden").css("visibility", "visible");
		$('.read-info-single').hide();

    } else {
        $(".menu-visible").css("visibility", "visible");
        $(".menu-hidden").css("visibility", "hidden");
			$('.read-info-single').hide();
			

    }

    a.toggle();

	 var b = $("#slideoutMenu1");
	if (b.is(":visible")) {
        $(".menu1-visible").css("visibility", "hidden");
		$(".menu1-hidden").css("visibility", "visible");
	} 
	$('#slideoutMenu1').hide();

	var d = $("#slideoutMenu2");
	if (d.is(":visible")) {
        $(".menu2-visible").css("visibility", "hidden");
		$(".menu2-hidden").css("visibility", "visible");
	} 
	$('#slideoutMenu2').hide();
}

