$(document).ready(function() {
    $("#hlFullLink").unbind("click").bind("click", function(a) {
        $.cookie("mob", "no", {
            path: "/"
        });
        var b = window.location.href.replace("/mb", "").replace("#", "");
        a.stopImmediatePropagation();
        window.location.href = b;
        return false;
    });
    $(".social-bar ul>li#twitter").after("<li id='twitterLink'><a href ='" + $("#twitter a").attr("href") + "' target='_blank'/></li>");
    $("#cboxClose").appendTo("#cboxWrapper");
    $(document).bind("cbox_open", function() {
        $("html,body").css("overflow", "hidden");
        $("#cboxOverlay").bind("touchmove", function() {
            return false;
        });
        $("#cboxTopLeft,#cboxTopCenter,#cboxTopRight,#cboxMiddleLeft,#cboxMiddleRight,#cboxBottomLeft,#cboxBottomCenter,#cboxBottomRight").bind("touchmove", function() {
            return false;
        });
    });
    $(document).bind("cbox_complete", function() {
        $(".ui-mobile .ui-page-active").css("overflow", "hidden");
        $(".ui-mobile .ui-page-active").height($("#cboxOverlay").height());
    });
    $(document).bind("cbox_closed", function() {
        $("html,body").css("overflow", "auto");
        $("#cboxOverlay").unbind("touchmove");
        $("#cboxTopLeft,#cboxTopCenter,#cboxTopRight,#cboxMiddleLeft,#cboxMiddleRight,#cboxBottomLeft,#cboxBottomCenter,#cboxBottomRight").unbind("touchmove");
        $("#cboxContent").unbind("touchmove");
        $(".ui-mobile .ui-page-active").css("overflow", "visible");
        $(".ui-mobile .ui-page-active").css("height", "auto");
    });
    $(".ui-collapsible-set .ui-collapsible-collapsed h3>a").click(function() {
        var a = $(this);
        var b = a.parent().parent().hasClass("ui-collapsible-collapsed");
        if (b) setTimeout(function() {
            $.mobile.silentScroll(a.offset().top);
        }, 200);
    });
    var a = window.location.hostname;
    var b = $("body>a[href='testurl']");
    if (b.length > 0 && window.top != window) b.parent().hide();
});
$(document).bind("pageshow", function() {
    $(".back-to-top").bind("vclick", function() {
        $("html, body").animate({
            scrollTop: 0
        }, 500);
        return false;
    });
    var a = true;
    $(".quick-action-nav a.menu").unbind("vclick").bind("vclick", function() {
        if (a) {
            toggleMenu();
            a = false;
            setTimeout(function() {
                a = true;
            }, 1e3);
        }

    });
		
	


    var b = true;
	
    $(".quick-action-nav a.menu1").unbind("vclick").bind("vclick", function() {
        if (b) {
            toggleMenu1();
            b = false;
            setTimeout(function() {
                b = true;
            }, 1e3);
        }
	 
    });
	

    var d = true;
	
    $(".quick-action-nav a.menu2").unbind("vclick").bind("vclick", function() {
        if (d) {
            toggleMenu2();
            d = false;
            setTimeout(function() {
                d = true;
            }, 1e3);
        }
	 
    });
	
});
